#!/usr/bin/env bash
set -euo pipefail


# TODO move me to another folder
# Compile everything

if nc -z localhost 8545; then
  echo "Port already used"
  # TODO aff flag to kill the process using the port
  exit 1
fi

# forge compile
anvil &
# ANVIL_PID=`lsof -i tcp:8545 | tail -n 1 | awk '{print $2}'`
ANVIL_PID=$!

echo "Waiting Anvil to launch on 8545..."


while ! nc -z localhost 8545; do
  sleep 0.1 # wait for 1/10 of the second before check again
done

echo "Anvil launched"
sleep 1

PROXY_

# cast rpc eth_getStorageAt --rpc-url http://127.0.0.1:8545 0x037A5D00E894d857Dd4eE9500ABa00032B5669BE
# cast rpc anvil_impersonateAccount --rpc-url http://127.0.0.1:8545 0x0000000000000000000000000000000000000000

# Set's the bytecode of a Poxy to the registry address
echo "Setting Registry Proxy"
cast rpc anvil_setCode --rpc-url http://127.0.0.1:8545 0x000000000000000000000000000000000000ce10 $PROXY_BYTECODE
# Sets the storage of the registry so that it has an owner we control
REGISTRY_OWNER_ADDRESS="f39Fd6e51aad88F6F4ce6aB8827279cffFb92266"
# pasition is bytes32(uint256(keccak256("eip1967.proxy.admin")) - 1);
echo "Setting Registry owner"
cast rpc anvil_setStorageAt --rpc-url http://127.0.0.1:8545 0x000000000000000000000000000000000000ce10 0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103 "0x000000000000000000000000$REGISTRY_OWNER_ADDRESS"


# set cheat codes, likely with another script


# run migrations
forge script migrations_sol/Migration.s.sol --rpc-url http://127.0.0.1:8545 -vvv --broadcast --skip-simulation --slow || echo "Migration script failed"

# Run integration tests
# TODO for some reason match path doesn't work
# forge test --fork-url http://127.0.0.1:8545 --match-contract=IntegrationTest -vvv # || echo "Test failed" # TODO for some reason the echo didn't work



echo "Killing Anvil"
if [[ -n $ANVIL_PID ]]; then
    kill $ANVIL_PID
fi